```js
import { EventEmitter } from "events";
 class SimplePushStream extends EventEmitter {
 constructor(data) {
 super();
 this.data = data;
 this.index = 0;
 }
 start() {
 this._pushNext();
 }
 _pushNext() {
 if (this.index >= this.data.length) {
 this.emit("end");
 return;
 }
 const chunk = this.data[this.index++];
 this.emit("data", chunk);
 setImmediate(() => this._pushNext());
 }
}
```

